import type { User, AvatarSize } from "./types"
import { AVATAR_COLORS } from "./types"

export function getInitials(user: User | string): string {
  if (typeof user === "string") {
    const names = user.trim().split(/\s+/)
    if (names.length === 1) {
      return names[0].charAt(0).toUpperCase()
    }
    return names[0].charAt(0).toUpperCase() + names[names.length - 1].charAt(0).toUpperCase()
  }

  const firstname = user.firstname?.trim() || ""
  const lastname = user.lastname?.trim() || ""
  const name = user.name?.trim() || ""
  const fullname = user.fullname?.trim() || ""

  if (firstname && lastname) {
    return firstname.charAt(0).toUpperCase() + lastname.charAt(0).toUpperCase()
  }

  if (name) {
    const nameParts = name.split(/\s+/)
    if (nameParts.length >= 2) {
      return nameParts[0].charAt(0).toUpperCase() + nameParts[nameParts.length - 1].charAt(0).toUpperCase()
    }
    return nameParts[0].charAt(0).toUpperCase()
  }

  if (fullname) {
    const fullnameParts = fullname.split(/\s+/)
    if (fullnameParts.length >= 2) {
      return fullnameParts[0].charAt(0).toUpperCase() + fullnameParts[fullnameParts.length - 1].charAt(0).toUpperCase()
    }
    return fullnameParts[0].charAt(0).toUpperCase()
  }

  return "?"
}

export function getFullName(user: User): string {
  if (user.fullname) return user.fullname
  if (user.name) return user.name
  if (user.firstname && user.lastname) return `${user.firstname} ${user.lastname}`
  if (user.firstname) return user.firstname
  if (user.lastname) return user.lastname
  return ""
}

export function getUserColor(user: User, index?: number): string {
  if (user.color) {
    // If color is provided with bg- prefix, use it as is
    if (user.color.startsWith("bg-")) {
      return user.color
    }
    // If color is just the color name, add bg- prefix
    return `bg-${user.color}`
  }

  // Generate deterministic color based on user ID or index
  const userId = user.userId || index || 0
  const colorIndex = typeof userId === "string" 
    ? userId.length % AVATAR_COLORS.length
    : Number(userId) % AVATAR_COLORS.length

  return AVATAR_COLORS[colorIndex]
}

export function resolveUser(
  userId: string | number, 
  users: User[] = []
): User | null {
  return users.find(user => user.userId === userId) || null
}

export function resolveUsers(
  userIds: (string | number)[], 
  users: User[] = []
): User[] {
  return userIds
    .map(id => resolveUser(id, users))
    .filter((user): user is User => user !== null)
}

export function validateImageUrl(url: string): boolean {
  try {
    const parsedUrl = new URL(url)
    return parsedUrl.protocol === "https:" || parsedUrl.protocol === "data:"
  } catch {
    return false
  }
}


export function getAvatarSizeClasses(size: AvatarSize): string {
  const sizeMap = {
    xs: "h-4 w-4 text-xs",
    sm: "h-5 w-5 text-xs", 
    md: "h-6 w-6 text-sm",
    lg: "h-7 w-7 text-sm",
    xl: "h-8 w-8 text-sm",
    "2xl": "h-9 w-9 text-base",
    "3xl": "h-10 w-10 text-base",
    "4xl": "h-12 w-12 text-lg",
    "5xl": "h-16 w-16 text-xl",
    max: "h-20 w-20 text-2xl"
  }
  return sizeMap[size]
}

export function getStatusIndicatorSize(size: AvatarSize): string {
  const statusSizeMap = {
    xs: "h-1 w-1",
    sm: "h-1.5 w-1.5",
    md: "h-2 w-2", 
    lg: "h-2 w-2",
    xl: "h-2.5 w-2.5",
    "2xl": "h-3 w-3",
    "3xl": "h-3 w-3", 
    "4xl": "h-4 w-4",
    "5xl": "h-5 w-5",
    max: "h-6 w-6"
  }
  return statusSizeMap[size]
}

import * as React from "react"
import { cn } from "@/lib/utils"
import { Avatar, AvatarFallback } from "./avatar"
import DocyIcon from "@/components/docy-icon"
import { type AvatarSize } from "./types"
import { getAvatarSizeClasses } from "./utils"

interface AvatarErrorBoundaryProps {
  children: React.ReactNode
  size?: AvatarSize
  className?: string
}

interface AvatarErrorBoundaryState {
  hasError: boolean
}

export class AvatarErrorBoundary extends React.Component<AvatarErrorBoundaryProps, AvatarErrorBoundaryState> {
  constructor(props: AvatarErrorBoundaryProps) {
    super(props)
    this.state = { hasError: false }
  }

  static getDerivedStateFromError(): AvatarErrorBoundaryState {
    return { hasError: true }
  }

  componentDidCatch(error: Error, errorInfo: React.ErrorInfo) {
    console.warn("DocyAvatar Error:", error, errorInfo)
  }

  render() {
    if (this.state.hasError) {
      const { size = "sm", className } = this.props

      return (
        <Avatar className={cn("bg-red-100", className)}>
          <AvatarFallback className={cn("bg-red-100 text-red-600", getAvatarSizeClasses(size))}>
            <DocyIcon 
              name="alert-circle" 
              size={size === "xs" || size === "sm" ? "xs" : size === "md" ? "sm" : "md"} 
              className="text-red-600"
            />
          </AvatarFallback>
        </Avatar>
      )
    }

    return this.props.children
  }
}

import * as React from "react"
import { cva, type VariantProps } from "class-variance-authority"
import { cn } from "@/lib/utils"
import { Avatar, AvatarImage, AvatarFallback } from "./avatar"
import DocyIcon from "@/components/docy-icon"
import type { User, AvatarSize, UserStatus } from "./types"
import { DEFAULT_AVATAR_SIZE, DEFAULT_MAX_ITEMS } from "./types"
import { 
  getInitials, 
  getFullName, 
  getUserColor, 
  resolveUser, 
  resolveUsers, 
  validateImageUrl, 
  getAvatarSizeClasses,
  getStatusIndicatorSize 
} from "./utils"
import { STATUS_COLORS } from "./types"
import { AvatarErrorBoundary } from "./error-boundary"

const avatarVariants = cva(
  "relative inline-flex shrink-0 overflow-hidden rounded-full",
  {
    variants: {
      size: {
        xs: "h-4 w-4",
        sm: "h-5 w-5", 
        md: "h-6 w-6",
        lg: "h-7 w-7",
        xl: "h-8 w-8",
        "2xl": "h-9 w-9",
        "3xl": "h-10 w-10",
        "4xl": "h-12 w-12",
        "5xl": "h-16 w-16",
        max: "h-20 w-20"
      }
    },
    defaultVariants: {
      size: "sm"
    }
  }
)

const avatarGroupVariants = cva(
  "flex items-center",
  {
    variants: {
      spacing: {
        default: "-space-x-2",
        tight: "-space-x-1",
        loose: "-space-x-3"
      }
    },
    defaultVariants: {
      spacing: "default"
    }
  }
)

export interface DocyAvatarProps
  extends Omit<React.HTMLAttributes<HTMLDivElement>, "size">,
    VariantProps<typeof avatarVariants> {
  src?: string
  userId?: string | number | (string | number)[]
  user?: User
  users?: User[]
  size?: AvatarSize
  maxItems?: number
  showUserStatus?: boolean
  status?: UserStatus
  fallbackSrc?: string
}

function SingleAvatar({
  user,
  src,
  size = DEFAULT_AVATAR_SIZE,
  showUserStatus = false,
  status,
  fallbackSrc,
  className
}: {
  user?: User
  src?: string
  size: AvatarSize
  showUserStatus: boolean
  status?: UserStatus
  fallbackSrc?: string
  className?: string
}) {
  const [imageError, setImageError] = React.useState(false)
  const [fallbackError, setFallbackError] = React.useState(false)

  const avatarClasses = cn(avatarVariants({ size }), className)
  const sizeClasses = getAvatarSizeClasses(size)

  const displayStatus = status || user?.status
  const initials = user ? getInitials(user) : "?"
  const bgColor = user ? getUserColor(user) : "bg-gray-500"
  const fullName = user ? getFullName(user) : ""

  const primarySrc = src || user?.photo
  const shouldShowImage = primarySrc && !imageError && validateImageUrl(primarySrc)
  const shouldShowFallback = fallbackSrc && !fallbackError && !shouldShowImage && validateImageUrl(fallbackSrc)

  return (
    <div className="relative">
      <Avatar className={avatarClasses}>
        {shouldShowImage && (
          <AvatarImage
            src={primarySrc}
            alt={fullName || "User avatar"}
            onError={() => setImageError(true)}
            className="object-cover"
          />
        )}

        {shouldShowFallback && (
          <AvatarImage
            src={fallbackSrc}
            alt={fullName || "User avatar"}
            onError={() => setFallbackError(true)}
            className="object-cover"
          />
        )}

        <AvatarFallback className={cn(bgColor, "text-white", sizeClasses)}>
          {user ? (
            <span className="font-medium">{initials}</span>
          ) : (
            <DocyIcon 
              name="user" 
              size={size === "xs" || size === "sm" ? "xs" : size === "md" ? "sm" : "md"} 
              className="text-white"
            />
          )}
        </AvatarFallback>
      </Avatar>

      {showUserStatus && displayStatus && (
        <span
          className={cn(
            "absolute -bottom-0.5 -right-0.5 block rounded-full ring-2 ring-white z-10",
            STATUS_COLORS[displayStatus],
            getStatusIndicatorSize(size)
          )}
          aria-label={`User is ${displayStatus}`}
        />
      )}
    </div>
  )
}

function MultipleAvatars({
  users,
  size = DEFAULT_AVATAR_SIZE,
  maxItems = DEFAULT_MAX_ITEMS,
  showUserStatus = false,
  className
}: {
  users: User[]
  size: AvatarSize
  maxItems: number
  showUserStatus: boolean
  className?: string
}) {
  const visibleUsers = users.slice(0, maxItems)
  const remainingCount = users.length - maxItems
  const sizeClasses = getAvatarSizeClasses(size)

  return (
    <div className={cn(avatarGroupVariants(), className)}>
      {visibleUsers.map((user, index) => (
        <div 
          key={user.userId || index}
          className="relative hover:z-10 hover:scale-105 transition-transform duration-150 origin-center"
        >
          <SingleAvatar
            user={user}
            size={size}
            showUserStatus={showUserStatus}
            className="ring-2 ring-white"
          />
        </div>
      ))}

      {remainingCount > 0 && (
        <Avatar className={cn(avatarVariants({ size }), "ring-2 ring-white bg-gray-100")}>
          <AvatarFallback className={cn("bg-gray-100 text-gray-600", sizeClasses)}>
            <span className="font-medium">+{remainingCount}</span>
          </AvatarFallback>
        </Avatar>
      )}
    </div>
  )
}

const DocyAvatar = React.forwardRef<HTMLDivElement, DocyAvatarProps>(
  ({ 
    src,
    userId,
    user,
    users = [],
    size = DEFAULT_AVATAR_SIZE,
    maxItems = DEFAULT_MAX_ITEMS,
    showUserStatus = false,
    status,
    className,
    fallbackSrc,
    ...props 
  }, ref) => {

    const renderContent = () => {
      // Handle multiple user IDs
      if (Array.isArray(userId)) {
        const resolvedUsers = resolveUsers(userId, users)

        if (resolvedUsers.length === 0) {
          // Show placeholder for empty group
          return (
            <Avatar className={avatarVariants({ size })}>
              <AvatarFallback className={getAvatarSizeClasses(size)}>
                <DocyIcon 
                  name="users" 
                  size={size === "xs" || size === "sm" ? "xs" : size === "md" ? "sm" : "md"} 
                  className="text-gray-400"
                />
              </AvatarFallback>
            </Avatar>
          )
        }

        if (resolvedUsers.length === 1) {
          return (
            <SingleAvatar
              user={resolvedUsers[0]}
              size={size}
              showUserStatus={showUserStatus}
              status={status}
              fallbackSrc={fallbackSrc}
            />
          )
        }

        return (
          <MultipleAvatars
            users={resolvedUsers}
            size={size}
            maxItems={maxItems}
            showUserStatus={showUserStatus}
          />
        )
      }

      // Handle single user ID
      if (userId) {
        const resolvedUser = resolveUser(userId, users)

        if (!resolvedUser) {
          // Show placeholder when user not found
          return (
            <Avatar className={avatarVariants({ size })}>
              <AvatarFallback className={getAvatarSizeClasses(size)}>
                <DocyIcon 
                  name="user" 
                  size={size === "xs" || size === "sm" ? "xs" : size === "md" ? "sm" : "md"} 
                  className="text-gray-400"
                />
              </AvatarFallback>
            </Avatar>
          )
        }

        return (
          <SingleAvatar
            user={resolvedUser}
            size={size}
            showUserStatus={showUserStatus}
            status={status}
            fallbackSrc={fallbackSrc}
          />
        )
      }

      // Handle direct user prop or src
      if (user || src) {
        return (
          <SingleAvatar
            user={user}
            src={src}
            size={size}
            showUserStatus={showUserStatus}
            status={status}
            fallbackSrc={fallbackSrc}
          />
        )
      }

      // Default placeholder
      return (
        <Avatar className={avatarVariants({ size })}>
          <AvatarFallback className={getAvatarSizeClasses(size)}>
            <DocyIcon 
              name="image" 
              size={size === "xs" || size === "sm" ? "xs" : size === "md" ? "sm" : "md"} 
              className="text-gray-400"
            />
          </AvatarFallback>
        </Avatar>
      )
    }

    return (
      <div ref={ref} {...props} className={className}>
        <AvatarErrorBoundary size={size}>
          {renderContent()}
        </AvatarErrorBoundary>
      </div>
    )
  }
)

